INSERT INTO `dynamic` VALUES (7, 'fever', '不行，我这么做了一段时间发现……这鬼网页真的要把servlet全部拆开来写的话……这怕是需要几十个servlet才够把……那不如和之前一样，在servlet里添加flag，在调用servlet的时候自动根据flag来做不同的事情！这就可以做到没有太多的servlet！', '2020-01-02-14-06-14_0.png', '2020-06-05 02:08:27');
INSERT INTO `dynamic` VALUES (6, 'fever', 'servlet添加起来复制粘贴非常省事，看起来这个项目很快就能做完！', NULL, '2020-06-04 02:06:56');
